-- ----------------------------
-- Table structure for article
-- ----------------------------
DROP TABLE IF EXISTS `article`;
CREATE TABLE `article` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL DEFAULT '' COMMENT '文章标题',
  `category_id` int(10) DEFAULT NULL COMMENT '分类id',
  `uri` varchar(255) DEFAULT '' COMMENT 'URL',
  `keywords` varchar(255) DEFAULT '' COMMENT '关键词',
  `tag` varchar(255) DEFAULT NULL COMMENT '标签',
  `summary` varchar(255) DEFAULT '' COMMENT '摘要',
  `content` longtext NOT NULL COMMENT '正文',
  `author` varchar(20) DEFAULT '' COMMENT '作者',
  `created` varchar(20) DEFAULT '0' COMMENT '发布时间',
  `viewnum` int(10) DEFAULT '0' COMMENT '阅读次数',
  `status` tinyint(1) DEFAULT '1' COMMENT '状态: 0草稿，1已发布',
  `visibility` tinyint(1) DEFAULT '1' COMMENT '状态: 0公开，1加密',
  `titlepic` varchar(255) DEFAULT NULL COMMENT '首页标题图片',
  PRIMARY KEY (`id`),
  KEY `INDEX_TCVS` (`title`,`created`,`viewnum`,`status`) USING BTREE,
  KEY `artical_category_id` (`category_id`),
  CONSTRAINT `artical_category_id` FOREIGN KEY (`category_id`) REFERENCES `category` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=41 DEFAULT CHARSET=utf8 COMMENT='文章';

